mapper.insert:
insert into em_user_info (user_id, user_name, telephone, 
  address, email, gender, 
  avatar_url, birthday, hometown,realname
  )
values (#{userId,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{telephone,jdbcType=VARCHAR}, 
  #{address,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{gender,jdbcType=BIT}, 
  #{avatarUrl,jdbcType=VARCHAR}, #{birthday,jdbcType=TIMESTAMP}, #{hometown,jdbcType=VARCHAR},
  #{realname, jdbcType=VARCHAR}
  )

mapper.select:
select uinfo.user_id from em_user_info uinfo,em_user_password userp
where uinfo.user_id=userp.user_id
and uinfo.user_name = #{username}
and userp.encrpt_password = #{password}
